(function( $ ){ $.fn.navegation = function(options) { var defaults = { 'modelo' : 'topo', 'efeitos': 'fade', 'velocidade': 'fast', 'tempo' : '0' }; var settings = $.extend( {}, defaults, options ); return this.each (function() { var scrollbar = $(this).offset().top; $(this).hide(); var elemento = $(this); $(window).scroll(function() { var scroll_top = $(window).scrollTop(); if (scroll_top > scrollbar) { switch (settings.modelo){ case "topo": $(elemento).css({'position': 'fixed', 'top':0,'left':0 }); break; case "baixo": $(elemento).css({'position': 'fixed', 'bottom':0,'left':0 }); break; case "esquerda": $(elemento).css({'position': 'fixed', 'left':5, 'top':'40%' }); break; case "direita": $(elemento).css({'position': 'fixed', 'right':5, 'top':'40%' }); break; } switch (settings.efeitos){ case "fade": $(elemento).delay(settings.tempo).fadeIn(settings.velocidade); break; case "deslizar": $(elemento).delay(settings.tempo).slideDown(settings.velocidade); break; case "mostrar": $(elemento).delay(settings.tempo).show(settings.velocidade); break; } } else { $(elemento).hide().stop(true, true); } }); }); }; })( jQuery );